CREATE TABLE `Collections` (
  `name` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `collectionID` int(11) NOT NULL,
  `type` varchar(30) NOT NULL,
  `englishTitle` varchar(200) NOT NULL,
  `arabicTitle` varchar(400) NOT NULL,
  `hasvolumes` varchar(3) NOT NULL,
  `hasbooks` varchar(3) NOT NULL,
  `haschapters` varchar(3) NOT NULL DEFAULT 'yes',
  `numhadith` int(11) NOT NULL,
  `totalhadith` int(11) DEFAULT NULL,
  `englishgrade1` varchar(100) DEFAULT NULL,
  `arabicgrade1` varchar(200) NOT NULL,
  `annotation` varchar(300) DEFAULT NULL,
  `shortintro` text NOT NULL,
  `about` text NOT NULL,
  `status` varchar(50) NOT NULL,
  `numberinginfodesc` text NOT NULL,
  PRIMARY KEY (`collectionID`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;
