  `hadithNumber` int(11) NOT NULL,
  `hadithText` text,
  `bookID` decimal(3,1) NOT NULL,
  `grade1` varchar(500) NOT NULL,
  `grade2` varchar(500) NOT NULL,
  `comments` text NOT NULL,
  `ourHadithNumber` int(11) NOT NULL,
  `matchingArabicURN` int(11) NOT NULL,
  `last_updated` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`englishURN`),
  UNIQUE KEY `englishURN_2` (`englishURN`),
  KEY `englishURN` (`englishURN`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;
